using System.Collections;using System.Collections.Generic;using UnityEngine;[RequireComponent(typeof(AudioSource))]public class AudioPlayer : MonoBehaviour {    public AudioClip clipToPlay,clipToPlayError;    public float delay;    private AudioSource audioSource;    private void Awake()    {        audioSource = GetComponent<AudioSource>();    }    public void PlaySound()    {        Invoke("_playSound", delay);    }	public void PlaySoundError()	{		Invoke("_playSoundError", delay);	}	private void _playSoundError()	{		if(clipToPlayError != null)			audioSource.PlayOneShot(clipToPlayError);	}    private void _playSound()    {        if(clipToPlay != null)            audioSource.PlayOneShot(clipToPlay);    }}